package javaAssigments;

import java.util.Random;
import java.util.Scanner;

public class GenerationOfRandomNumbers {
	
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int q;
		Random scan=new Random();
		System.out.println("enter the random numbers");
		
		
		for(int i=1;i<=10;i++){
			q=1+scan.nextInt(5);
			
		System.out.println("entered numbers are "+q);
		
			

	}

}}
